def evaluate(candidate_solution, structured_data, reference_solution):
    import json

    # Array indices for aircraft data
    EARLIEST = 0
    TARGET = 1
    LATEST = 2
    EARLY_PENALTY = 3
    LATE_PENALTY = 4

    # 1. Parse JSON
    try:
        solution = json.loads(candidate_solution) if isinstance(candidate_solution, str) else candidate_solution
    except:
        return {"is_valid": False, "validation_errors": ["Invalid JSON format"], "optimality_score": 0.0}

    # 2. Check required fields
    required_fields = ["landing_times", "total_penalty"]
    missing = [f for f in required_fields if solution is None or f not in solution]
    if missing:
        return {"is_valid": False, "validation_errors": [f"Missing fields: {missing}"], "optimality_score": 0.0}

    # 3. Core validation and calculation (wrapped in try-catch)
    try:
        landing_times = solution["landing_times"]
        total_penalty = solution["total_penalty"]

        num_aircraft = structured_data["num_aircraft"]
        aircraft = structured_data["aircraft"]
        separation_matrix = structured_data["separation_matrix"]
        errors = []

        # Basic structure checks
        if len(landing_times) != num_aircraft:
            errors.append(f"landing_times length mismatch: expected {num_aircraft}, got {len(landing_times)}")
            return {"is_valid": False, "validation_errors": errors, "optimality_score": 0.0}

        # 1. Time window constraints
        for i in range(num_aircraft):
            landing_time = landing_times[i]
            earliest = aircraft[i][EARLIEST]
            latest = aircraft[i][LATEST]
            if landing_time < earliest or landing_time > latest:
                errors.append(f"Aircraft {i}: landing time {landing_time} outside window [{earliest}, {latest}]")

        # 2. Separation constraints (single runway)
        for i in range(num_aircraft):
            for j in range(num_aircraft):
                if i != j:
                    lt_i = landing_times[i]
                    lt_j = landing_times[j]
                    if lt_i <= lt_j:
                        required_gap = separation_matrix[i][j]
                        actual_gap = lt_j - lt_i
                        if actual_gap < required_gap:
                            errors.append(f"Separation violation: aircraft {i} and {j} (gap: {actual_gap}, required: {required_gap})")

        # 3. Penalty calculation validation
        calculated_penalty = 0.0
        for i in range(num_aircraft):
            landing_time = landing_times[i]
            target = aircraft[i][TARGET]
            if landing_time < target:
                calculated_penalty += (target - landing_time) * aircraft[i][EARLY_PENALTY]
            elif landing_time > target:
                calculated_penalty += (landing_time - target) * aircraft[i][LATE_PENALTY]

        # Check penalty consistency
        if abs(calculated_penalty - total_penalty) > 1e-6:
            errors.append(f"Penalty inconsistency: calculated {calculated_penalty}, declared {total_penalty}")

        # Calculate optimality score
        optimal_penalty = reference_solution["total_penalty"]
        if optimal_penalty == 0:
            optimality_score = 1.0 if total_penalty == 0 else 0.0
        else:
            gap = abs(optimal_penalty - total_penalty) / abs(optimal_penalty)
            optimality_score = max(0, 1 - gap)

        return {
            "is_valid": len(errors) == 0,
            "validation_errors": errors,
            "optimality_score": optimality_score
        }

    except Exception as e:
        return {"is_valid": False, "validation_errors": ["Format error in solution data"], "optimality_score": 0.0}
