def evaluate(candidate_solution, structured_data, reference_solution):
    import json

    try:
        solution = json.loads(candidate_solution) if isinstance(candidate_solution, str) else candidate_solution
    except:
        return {"is_valid": False, "validation_errors": ["Invalid JSON format"], "optimality_score": 0.0}

    required_fields = ["selected_people", "group_size"]
    missing = [f for f in required_fields if solution is None or f not in solution]
    if missing:
        return {"is_valid": False, "validation_errors": [f"Missing fields: {missing}"], "optimality_score": 0.0}

    try:
        selected_people = solution["selected_people"]
        group_size = solution["group_size"]
        friendships = structured_data["friendships"]
        num_people = structured_data["num_people"]
        errors = []

        if group_size != len(selected_people):
            errors.append(f"Group size mismatch: declared {group_size}, actual {len(selected_people)}")

        for person in selected_people:
            if person < 0 or person >= num_people:
                errors.append(f"Invalid person ID: {person}")

        if len(selected_people) != len(set(selected_people)):
            errors.append("Duplicate people in selection")

        adj = set()
        for u, v in friendships:
            adj.add((u, v))
            adj.add((v, u))

        for i in range(len(selected_people)):
            for j in range(i + 1, len(selected_people)):
                u, v = selected_people[i], selected_people[j]
                if (u, v) not in adj:
                    errors.append(f"Not a clique: {u} and {v} are not friends")

        if not errors:
            optimal_size = reference_solution["group_size"]
            if optimal_size == 0:
                optimality_score = 1.0 if group_size == 0 else 0.0
            else:
                optimality_score = group_size / optimal_size
        else:
            optimality_score = 0.0

        return {
            "is_valid": len(errors) == 0,
            "validation_errors": errors,
            "optimality_score": optimality_score
        }

    except Exception as e:
        return {"is_valid": False, "validation_errors": ["Format error in solution data"], "optimality_score": 0.0}
