def evaluate(candidate_solution, structured_data, reference_solution):
    import json

    try:
        solution = json.loads(candidate_solution) if isinstance(candidate_solution, str) else candidate_solution
    except:
        return {"is_valid": False, "validation_errors": ["Invalid JSON format"], "optimality_score": 0.0}

    required_fields = ["selected_scientists", "team_size"]
    missing = [f for f in required_fields if solution is None or f not in solution]
    if missing:
        return {"is_valid": False, "validation_errors": [f"Missing fields: {missing}"], "optimality_score": 0.0}

    try:
        selected_scientists = solution["selected_scientists"]
        team_size = solution["team_size"]
        collaborations = structured_data["collaborations"]
        num_scientists = structured_data["num_scientists"]
        errors = []

        if len(selected_scientists) != team_size:
            errors.append(f"Team size mismatch: declared {team_size}, actual {len(selected_scientists)}")

        for scientist in selected_scientists:
            if scientist < 0 or scientist >= num_scientists:
                errors.append(f"Invalid scientist ID: {scientist}")

        if len(selected_scientists) != len(set(selected_scientists)):
            errors.append("Duplicate scientists in selection")

        adj = set()
        for u, v in collaborations:
            adj.add((u, v))
            adj.add((v, u))  

        for i in range(len(selected_scientists)):
            for j in range(i + 1, len(selected_scientists)):
                u, v = selected_scientists[i], selected_scientists[j]
                if (u, v) not in adj:
                    errors.append(f"Not a clique: {u} and {v} have not collaborated")

        if len(errors) > 0:
            optimality_score = 0.0
        else:
            optimal_size = reference_solution["team_size"]
            if optimal_size == 0:
                optimality_score = 1.0 if team_size == 0 else 0.0
            else:
                gap = abs(optimal_size - team_size) / abs(optimal_size)
                optimality_score = max(0, 1 - gap)

        return {
            "is_valid": len(errors) == 0,
            "validation_errors": errors,
            "optimality_score": optimality_score
        }

    except Exception as e:
        return {"is_valid": False, "validation_errors": ["Format error in solution data"], "optimality_score": 0.0}
