def evaluate(candidate_solution, structured_data, reference_solution):
    import json


    try:
        solution = json.loads(candidate_solution) if isinstance(candidate_solution, str) else candidate_solution
    except:
        return {"is_valid": False, "validation_errors": ["Invalid JSON format"], "optimality_score": 0.0}


    required_fields = ["selected_topics", "num_selected_topics"]
    missing = [f for f in required_fields if solution is None or f not in solution]
    if missing:
        return {"is_valid": False, "validation_errors": [f"Missing fields: {missing}"], "optimality_score": 0.0}


    try:
        selected_topics = solution["selected_topics"]
        num_selected_topics = solution["num_selected_topics"]
        conflict_pairs = structured_data["conflict_pairs"]
        num_topics = structured_data["num_topics"]
        valid_topics = set(range(num_topics))
        errors = []


        if not isinstance(selected_topics, list):
            errors.append("selected_topics must be a list")

        if num_selected_topics != len(selected_topics):
            errors.append(f"num_selected_topics inconsistency: declared {num_selected_topics}, actual {len(selected_topics)}")


        for topic in selected_topics:
            if topic not in valid_topics:
                errors.append(f"Topic {topic} does not exist in problem")


        if len(selected_topics) != len(set(selected_topics)):
            errors.append("Duplicate topics in selected_topics")


        edge_set = set()
        for edge in conflict_pairs:
            u, v = edge[0], edge[1]
            edge_set.add((min(u, v), max(u, v)))


        for i in range(len(selected_topics)):
            for j in range(i + 1, len(selected_topics)):
                u, v = selected_topics[i], selected_topics[j]
                if (min(u, v), max(u, v)) in edge_set:
                    errors.append(f"Independence constraint violated: conflict exists between topics {u} and {v}")


        if len(errors) > 0:
            return {
                "is_valid": False,
                "validation_errors": errors,
                "optimality_score": 0.0
            }


        optimal_size = reference_solution["num_selected_topics"]
        if optimal_size == 0:
            optimality_score = 1.0 if num_selected_topics == 0 else 0.0
        else:
            gap = abs(optimal_size - num_selected_topics) / abs(optimal_size)
            optimality_score = max(0, 1 - gap)

        return {
            "is_valid": True,
            "validation_errors": [],
            "optimality_score": optimality_score
        }

    except Exception as e:
        return {"is_valid": False, "validation_errors": ["Format error in solution data"], "optimality_score": 0.0}
